function appendHead(tpl) {
  // Prepare stylesheet of custom CSS properties
  const tplElement = document.createElement('template');
  tplElement.innerHTML = tpl;

  // Add templates to the document
  document.head.appendChild(tplElement.content);
}

var commonTpl = "<style> :root {\n\n  /* ==========================================================================\n     Variables\n     ========================================================================== */\n  --white: rgb(250, 250, 250);\n  --black: #000;\n\n  --myuw-grey-50: #f5f5f5;\n  --myuw-grey-100: #e5e5e5;\n  --myuw-grey-200: #d8d8d8;\n  --myuw-grey-300: #ccc;\n  --myuw-grey-400: #adadad;\n  --myuw-grey-500: #999;\n  --myuw-grey-600: #8c8c8c;\n  --myuw-grey-700: #696969;\n  --myuw-grey-800: #404040;\n  --myuw-grey-900: #333;\n  --myuw-grey-a100: #f5f5f5;\n  --myuw-grey-a200: #e5e5e5;\n  --myuw-grey-a400: #adadad;\n  --myuw-grey-a700: #696969;\n\n  /* Media queries */\n  --xs: 600px;\n  --sm: 960px;\n  --md: 1280px;\n  --lg: 1920px;\n\n  /* Shadow depths */\n  --box-shadow-1: 0 1px 3px rgba(0, 0, 0, 0.12), 0 1px 2px rgba(0, 0, 0, 0.24);\n  --box-shadow-2: 0 3px 6px rgba(0, 0, 0, 0.16), 0 3px 6px rgba(0, 0, 0, 0.23);\n  --box-shadow-3: 0 10px 20px rgba(0, 0, 0, 0.19), 0 6px 6px rgba(0, 0, 0, 0.23);\n  --box-shadow-4: 0 14px 28px rgba(0, 0, 0, 0.25), 0 10px 10px rgba(0, 0, 0, 0.22);\n  --box-shadow-5: 0 19px 38px rgba(0, 0, 0, 0.3), 0 15px 12px rgba(0, 0, 0, 0.22);\n\n  /* z-index values for dialogs/toasts/tooltips (top-level elements) */\n  --dialog-depth-1: 101;\n  --dialog-depth-2: 100;\n\n  /* z-index values for elements that sit on top of focus trap */\n  --sidenav-depth: 59;\n\n  /* z-index values for top bar elements */\n  --top-bar-1: 55;\n  --top-bar-2: 54;\n  --top-bar-3: 53;\n\n  /* z-index values for widget elements */\n  --widget-depth-1: 52;\n  --widget-depth-2: 51;\n\n  /* Footer */\n  --footer-background-color: #404040;\n  --footer-text-color: #adadad;\n\n  /*\n    Transitions\n  */\n  --background-transition: background-color 0.4s cubic-bezier(0.25, 0.8, 0.25, 1);\n  --transition-height: height 0.4s cubic-bezier(0.25, 0.8, 0.25, 1);\n  --transition-width: width 0.4s cubic-bezier(0.25, 0.8, 0.25, 1);\n  --transition-opacity: opacity 0.4s cubic-bezier(0.25, 0.8, 0.25, 1);\n  --transition-opacity-fast: opacity 0.3s cubic-bezier(0.25, 0.8, 0.25, 1);\n  --transition-all: all 0.4s cubic-bezier(0.25, 0.8, 0.25, 1);\n  --transition-color: color 0.4s cubic-bezier(0.25, 0.8, 0.25, 1);\n\n  /* Font(s) */\n  --font-family: Roboto, Helvetica Neue, sans-serif;\n\n} </style> ";

var colorsTpl = "<style> :root {\n  /* MyUW-Madison colors */\n  --myuw-madison-red-50: #fed5d7;\n  --myuw-madison-red-100: #fc8b8f;\n  --myuw-madison-red-200: #fb545a;\n  --myuw-madison-red-300: #f90e17;\n  --myuw-madison-red-400: #e3060e;\n  --myuw-madison-red-500: #c5050c;\n  --myuw-madison-red-600: #a7040a;\n  --myuw-madison-red-700: #890308;\n  --myuw-madison-red-800: #6b0307;\n  --myuw-madison-red-900: #4e0205;\n  --myuw-madison-red-a100: #fed5d7;\n  --myuw-madison-red-a200: #fc8b8f;\n  --myuw-madison-red-a400: #e3060e;\n  --myuw-madison-red-a700: #890308;\n\n  --myuw-madison-blue-50: #b8e9fd;\n  --myuw-madison-blue-100: #6dd3fc;\n  --myuw-madison-blue-200: #36c2fa;\n  --myuw-madison-blue-300: #05a4e4;\n  --myuw-madison-blue-400: #058fc6;\n  --myuw-madison-blue-500: #0479a8;\n  --myuw-madison-blue-600: #03638a;\n  --myuw-madison-blue-700: #034e6c;\n  --myuw-madison-blue-800: #02384e;\n  --myuw-madison-blue-900: #012330;\n  --myuw-madison-blue-a100: #b8e9fd;\n  --myuw-madison-blue-a200: #058fc6;\n  --myuw-madison-blue-a400: #0479a8;\n  --myuw-madison-blue-a700: #034e6c;\n\n  --myuw-madison-orange-50: #ffffff;\n  --myuw-madison-orange-100: #f9e7d7;\n  --myuw-madison-orange-200: #f2c9a6;\n  --myuw-madison-orange-300: #eaa368;\n  --myuw-madison-orange-400: #e6934d;\n  --myuw-madison-orange-500: #e28332;\n  --myuw-madison-orange-600: #d7731e;\n  --myuw-madison-orange-700: #bc651b;\n  --myuw-madison-orange-800: #a15717;\n  --myuw-madison-orange-900: #874813;\n  --myuw-madison-orange-a100: #ffffff;\n  --myuw-madison-orange-a200: #f9e7d7;\n  --myuw-madison-orange-a400: #e6934d;\n  --myuw-madison-orange-a700: #bc651b;\n} </style> ";

var themeTpl = "<style> :root {\n    /*\n     * You can use these generic variables in your elements for easy theming.\n     * For example, if all your elements use `--primary-text-color` as its main\n     * color, then switching from a light to a dark theme is just a matter of\n     * changing the value of `--primary-text-color` in your application.\n     */\n    --primary-text-color: var(--light-theme-text-color);\n    --primary-background-color: var(--light-theme-background-color);\n    --secondary-text-color: var(--light-theme-secondary-color);\n    --disabled-text-color: var(--light-theme-disabled-color);\n    --divider-color: var(--light-theme-divider-color);\n    --error-color: var(--myuw-madison-orange-a700);\n    /*\n     * Primary and accent colors. Also see color.html for more colors.\n     */\n    --primary-color: var(--myuw-madison-red-500);\n    --light-primary-color: var(--myuw-madison-red-100);\n    --dark-primary-color: var(--myuw-madison-red-700);\n    --accent-color: var(--myuw-madison-blue-a200);\n    --light-accent-color: var(--myuw-madison-blue-a100);\n    --dark-accent-color: var(--myuw-madison-blue-a400);\n    /*\n     * Material Design Light background theme\n     */\n    --light-theme-background-color: var(--myuw-grey-100);\n    --light-theme-base-color: var(--black);\n    --light-theme-text-color: var(--myuw-grey-900);\n    --light-theme-secondary-color: var(--myuw-grey-700);  /* for secondary text and icons */\n    --light-theme-disabled-color: var(--myuw-grey-500);  /* disabled/hint text */\n    --light-theme-divider-color: var(--myuw-grey-300);\n    /*\n     * Material Design Dark background theme\n     */\n    --dark-theme-background-color: var(--paper-grey-900);\n    --dark-theme-base-color: var(--myuw-grey-100);\n    --dark-theme-text-color: var(--myuw-grey-100);\n    --dark-theme-secondary-color: var(--myuw-grey-300);  /* for secondary text and icons */\n    --dark-theme-disabled-color: var(--myuw-grey-700);  /* disabled/hint text */\n    --dark-theme-divider-color: var(--myuw-grey-900);\n    /*\n     * Deprecated values because of their confusing names.\n     */\n    --text-primary-color: var(--dark-theme-text-color);\n    --default-primary-color: var(--primary-color);\n\n\n    /* opacity for dark text on a light background */\n    --dark-divider-opacity: 0.12;\n    --dark-disabled-opacity: 0.38; /* or hint text or icon */\n    --dark-secondary-opacity: 0.54;\n    --dark-primary-opacity: 0.87;\n    /* opacity for light text on a dark background */\n    --light-divider-opacity: 0.12;\n    --light-disabled-opacity: 0.3; /* or hint text or icon */\n    --light-secondary-opacity: 0.7;\n    --light-primary-opacity: 1.0;\n  } </style> ";

appendHead(commonTpl);
appendHead(colorsTpl);
appendHead(themeTpl);
